import {React ,createContext, useReducer, useContext} from 'react'
const initalValue=  {
  whatched:[],
  whatchlist:[]
}
 const ContextApi =createContext(initalValue)
function Globalcontext() {
    const reducer =(state,action) =>{
    switch (action.type){
      case "ADD_TO_WHATCHLIST" :
        return{
          ...state,
          whatchlist:[action.payload, ...state.whatchlist]
        }
        case "REMOVE_FROM_WHATCHLIST" :
          return{
            ...state,
            whatchlist:state.whatchlist.filter(movie => movie.imdbID !== action.payload)
          }
          default :
          return state
          
    }
  }
  let [state , dispatch] = useReducer(reducer ,initalValue)
  console.log(dispatch)

  return (
  <ContextApi.Provider value={{
whatched:state.whatched,
dispatch:dispatch,

whatchlist:state.whatchlist,
  }}>

  </ContextApi.Provider>
  )
}
export const Usecontext = () =>{
  return useContext(ContextApi);
}

export default Globalcontext
